mapper.select:
SELECT id ,username ,pwd ,tel ,area ,church ,longitude ,latitude ,serve_days_pre_forth * 1 as serve_days_pre_forth ,serve_days_fifth * 1 as serve_days_fifth FROM user
and area like '%${area}%'
and church like '%${church}%'
and serve_days_pre_forth = #{serve_days_pre_forth}
and serve_days_fifth like = #{serve_days_fifth}

mapper.insert:
insert into servicearrange
area, church, reuniondate, reuniondateCN, weekofdate
#{item.area}, #{item.church}, #{item.reunion_date}, #{item.reunion_dateCN}, #{item.weekofdate}

mapper.select:
SELECT a.id, a.area, a.church,
b.username, CONCAT(b.area, '**', b.church) as fromChurch, a.reuniondate, a.reuniondateCN, a.arrangestatus,
case a.weekofdate WHEN '1' THEN '第一周' WHEN '2' THEN '第二周' WHEN '3' THEN '第三周' WHEN '4' THEN '第四周' WHEN '5' THEN '第五周'
END AS weekofdate FROM servicearrange a LEFT JOIN `user` b ON b.id = a.user_id
and a.reuniondate = #{reuniondate}
and a.area = #{area}
and a.church = #{church}
and b.username = #{username}
and a.arrangestatus = #{arrangestatus}

mapper.update:
 		update 
    		servicearrange 
    	set
    		user_id = #{user_id}
	 	where id = #{id}
